import React, {Component} from 'react'
import './App.css'

class QuoteGenerator extends Component{
    constructor(){
        super()
        this.state = {
            quote: "Default quote",
            author: "Default author",
            allQuotes: []
        }
        this.handleClick = this.handleClick.bind(this)
    }

    // healthruwords.p.rapidapi.com

    componentDidMount() {
        fetch('http://quotes.stormconsultancy.co.uk/quotes.json')
            .then(response => response.json())
            .then(data => (console.log(data)))
            .then(data => {
                const newQuotes = data
                this.setState({allQuotes: newQuotes})})
            .catch(err => {console.log(err)})
        console.log(this.state.allQuotes)
    }

    handleClick(event){
        this.setState({
            quote: "New Quote!",
            author: "New Author!"
        })
    }

    render(){
        return(
            <div>
                <div id="quote-box">
                    <div id="text">
                        <p>
                            {this.state.quote}
                            
                        </p>
                    </div>
                    <div id="author">
                        <p>
                            {this.state.author}
                        </p>
                    </div>
                    <button id="new-quote" onClick={this.handleClick}>New Quote</button>
                    <a href="twitter.com" id="tweet-quote">Tweet Quote</a>
                </div>
            </div>
        )
    }

}

export default QuoteGenerator
