import React, {Component} from 'react'
import './App.css'

class QuoteGenerator extends Component{
    constructor(){
        super()
        this.state = {
            quote: "Default quote",
            author: "Default author"
        }
    }

    render(){
        return(
            <div>
                <div id="quote-box">
                    <div id="text">
                        <p>
                            {this.state.quote}
                            
                        </p>
                    </div>
                    <div id="author">
                        <p>
                        {this.state.author}
                        </p>
                    </div>
                    <button id="new-quote">New Quote</button>
                    <a href="#" id="tweet-quote">Tweet Quote</a>
                </div>
            </div>
        )
    }

}

export default QuoteGenerator
