import React, {Component} from 'react'
import './App.css'

class QuoteGenerator extends Component{
    constructor(){
        super()
        this.state = {
            quote: "Default quote",
            author: "Default author",
            allQuotes: []
        }
        this.handleClick = this.handleClick.bind(this)
    }

    // healthruwords.p.rapidapi.com

    componentDidMount() {
        fetch("https://healthruwords.p.rapidapi.com/v1/quotes/?id=731&t=Wisdom&maxR=1&size=medium", {
	    "method": "GET",
	    "headers": {
		    "x-rapidapi-host": "healthruwords.p.rapidapi.com",
		    "x-rapidapi-key": "6c26722765msh90b4b0c04761e11p1765c4jsn88f7ec578f91"
	        }
        })
        .then(response => {
            console.log(response);
        })

        .then(response => response.json())
            .then(data => {this.setState({allQuotes: data})
            })
            .catch(err => {
                console.log(err);
            });
        console.log(this.state)
    }

    handleClick(event){
        this.setState({
            quote: "New Quote!",
            author: "New Author!"
        })
    }

    render(){
        return(
            <div>
                <div id="quote-box">
                    <div id="text">
                        <p>
                            {this.state.quote}
                            
                        </p>
                    </div>
                    <div id="author">
                        <p>
                            {this.state.author}
                        </p>
                    </div>
                    <button id="new-quote" onClick={this.handleClick}>New Quote</button>
                    <a href="#" id="tweet-quote">Tweet Quote</a>
                </div>
            </div>
        )
    }

}

export default QuoteGenerator
