import React, {Component} from 'react'
import './App.css'

class QuoteGenerator extends Component{
    constructor(){
        super()
        this.state = {
            quote: "Default quote",
            author: "Default author",
            allQuotes: []
        }
        this.handleClick = this.handleClick.bind(this)
    }

    componentDidMount() {
        fetch('http://quotes.stormconsultancy.co.uk/quotes.json')
            .then(response => response.json())
            .then(data => {
                console.log(data)
                this.setState({allQuotes: data})
            })
            .catch(err => {console.log(err)})
        
    }

    handleClick(event){
        event.preventDefault()
        console.log(this.state)
        const randNum = Math.floor(Math.random() * this.state.allQuotes.length)
        const randQuote = this.state.allQuotes[randNum].quote
        const randAuthor = this.state.allQuotes[randNum].author
        this.setState({ quote: randQuote, author: randAuthor })
    }

    render(){
        return(
            <div>
                <div id="quote-box">
                    <div id="text">
                        <p>
                            {this.state.quote}
                            
                        </p>
                    </div>
                    <div id="author">
                        <p>
                            {this.state.author}
                        </p>
                    </div>
                    <button id="new-quote" onClick={this.handleClick}>New Quote</button>
                    <a href="twitter.com/intent/tweet?text=${this.state.quote}" id="tweet-quote">Tweet Quote</a>
                </div>
            </div>
        )
    }

}

export default QuoteGenerator
